import React from 'react';
import { ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import { ContractData, ContractForm} from 'drizzle-react-components';
import TweetForm from './TweetForm';
import SingleTweet from './SingleTweet';
import { Drizzle } from 'drizzle';
import options from './drizzleOptions';
import { DrizzleContext } from "drizzle-react";

// const drizzle = new Drizzle(options);
export default class tweets extends React.Component {
  constructor(props) {
    super(props);
    this.state = { userAddress: '',
    tweet: "",
    hashT: "",
    numTweets: 0
  };
  }
  async componentDidMount() {
    // console.log("drizzle ", drizzle)
    const accounts = await this.props.drizzle.web3.eth.getAccounts();
    const numTweets = await this.props.drizzle.contracts.Twittor.methods.getNumTweets().call();

    this.setState({ userAddress: accounts[0], numTweets });
  }

  handleInputChange = (event) => {
    this.setState({
      tweet: event.target.value,
      hashT: this.findHashTag(event.target.value),
    });
    console.log(this.state);
  }

  handleSubmit = (event) => {
  event.preventDefault();


  this.props.drizzle.contracts.Twittor.methods.addTweetStruct(this.state.tweet, this.state.hashT).send({from: this.state.userAddress})


}

 getTweet = async (index) => {
  console.log("props ", this.props)
  const result =  await this.props.drizzle.contracts.Twittor.methods.getEverythingTweetStruct(this.state.userAddress, index).call();
  console.log("TWEET", result);
}

findHashTag(str) {
  return str.split(' ').filter(word => {
    return word[0] === '#';
  })[0];
}

  render() {
    let length = this.state.numTweets;
    // const getNumTweetsFirstKey = Object.keys(
    //   this.props.drizzleState.contracts.Twittor.getNumTweets
    // )[1];
    // if (this.props.drizzleState.contracts.Twittor.getNumTweets[getNumTweetsFirstKey]) {
    //   length = this.props.drizzleState.contracts.Twittor.getNumTweets[getNumTweetsFirstKey].value;
    // }
    let mapArray = [];
    if (length) {
      mapArray.length = length;
      mapArray.fill(1);
    }

    console.log("props", this.props)

    return (
      <div className="App">
        <ToastContainer />

        <div>
          <button onClick = {this.getTweet} >GET TWEET</button>
          <h1>TWEETS</h1>
          <form onSubmit = {this.handleSubmit}>
          <input
              key="tweet"
              name="tweet"
              value={this.state.tweet}
              placeholder="tweet"
              onChange={this.handleInputChange}
            />
            <button type="submit">TWEET</button>
          </form>
          {/* <TweetForm contract="Twittor" method="addTweetStruct" store = {this.props.drizzle}/> */}
          <div className="allTweets">
            {mapArray
              .map((tweet, idx) => {
                return (
                  <SingleTweet
                    address={this.state.userAddress}
                    index={idx}
                    key={idx}
                  />
                );
              })
              .reverse()}
          </div>
          <div className="hide">
            {/* {
              <ContractData
                contract="Twittor"
                method="getNumTweets"
                methodArgs={[this.state.userAddress]}
              />
            } */}
          </div>

          {/* (
