import React from 'react';
import { ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import { ContractData } from 'drizzle-react-components';
import TweetForm from './TweetForm';
import SingleTweet from './SingleTweet';
import { Drizzle } from 'drizzle';
import options from './drizzleOptions';

const drizzle = new Drizzle(options);

export default class tweets extends React.Component {
  getData = async (address, index) => {
    const result = await drizzle.contracts.Twittor.methods
      .getEverythingTweetStruct(address, index)
      .call();
    return result;
  };

  render() {
    let length = 0;
    const userAddress = this.props.accounts[0];
    const getNumTweetsFirstKey = Object.keys(
      this.props.Twittor.getNumTweets
    )[0];
    if (this.props.Twittor.getNumTweets[getNumTweetsFirstKey]) {
      length = this.props.Twittor.getNumTweets[getNumTweetsFirstKey].value;
    }
    // console.log("Store ", this.props)
    // console.log('length ', length)
    let mapArray = [];
    if (length) {
      mapArray.length = length;
      mapArray.fill(1);
    }

    return (
      <div className="App">
        <ToastContainer />

        <div>
          <h1>Test</h1>
          <h1>TWEETS</h1>
          <TweetForm contract="Twittor" method="addTweetStruct" />
          <div className="allTweets">
            {mapArray
              .map((tweet, idx) => {
                return <SingleTweet tweet={this.getData(userAddress, idx)} />;
              })
              .reverse()}
          </div>
          <div className="hide">
            {
              <ContractData
                contract="Twittor"
                method="getNumTweets"
                methodArgs={[userAddress]}
              />
            }
          </div>
        </div>
      </div>
    );
  }
}
