import React from 'react';
import { Statistic } from 'semantic-ui-react';

export default class UserPage extends React.Component {
  constructor(props, context) {
    super(props);
    this.drizzleState = context.drizzle;
    this.state = {
      userAddress: '',
      numTweets: 0,
      numReplies: 0,
      following: 0,
      followers: 0,
    };
  }

  fetchNumberOfTweets = async address => {
    const numTweets = await this.props.drizzle.contracts.Twittor.methods
      .getNumTweets(address)
      .call();
    this.setState({ numTweets: numTweets });
  };

  fetchNumberOfReplies = async address => {
    console.log('running?');
    const batch = [];
    for (let i = 0; i < this.state.numTweets; i++) {
      batch.push(
        this.props.drizzle.contracts.Twittor.methods
          .getNumReplies(address, i)
          .call()
      );
    }
    console.log('still running?');
    const numReplies = await Promise.all(batch);
    console.log('is it stopping here?');
    const numRepliesTotal = numReplies
      .filter(replyNum => replyNum > 0)
      .reduce((acc, curr) => acc + +curr, 0);
    console.log(numReplies, ' num replies');
    this.setState({ numReplies: numRepliesTotal });
  };

  fetchNumberOfFollowing = async address => {
    let count = 0;
    let currFollowing = await this.props.drizzle.contracts.Twittor.methods.viewFollowing(
      address,
      count
    );
    console.log(currFollowing);
    // do {
    //   currFollowing = await this.props.drizzle.contracts.Twittor.methods.viewFollowing(
    //     address,
    //     count
    //   );
    //   count++;
    // } while (currFollowing);

    // this.setState({ following: count });
  };

  async componentDidMount() {
    const { drizzle } = this.props;
    const accounts = await drizzle.web3.eth.getAccounts();
    this.setState({ userAddress: accounts[0] });

    await this.fetchNumberOfTweets(accounts[0]);
    console.log('is this working?');
    // await this.fetchNumberOfReplies(accounts[0]);
    // await this.fetchNumberOfFollowing(accounts[0]);
  }

  populateData = () => {
    return [
      { key: 'numTweets', label: 'Tweets', value: `${this.state.numTweets}` },
      {
        key: 'numReplies',
        label: 'Replies',
        value: `${this.state.numReplies}`,
      },
      {
        key: 'following',
        label: 'Following',
        value: `${this.state.following}`,
      },
      {
        key: 'followers',
        label: 'Followers',
        value: `${this.state.followers}`,
      },
    ];
  };

  render() {
    return (
      <div>
        <h1>User Page</h1>
        <h3>Address: {this.state.userAddress}</h3>
        <div
          style={{
            display: 'flex',
            justifyContent: 'center',
          }}
        >
          <Statistic.Group items={this.populateData()} />
        </div>
      </div>
    );
  }
}
