import React from 'react';
import { Drizzle } from 'drizzle';
import options from './drizzleOptions';

const drizzle = new Drizzle(options);
const getData = async (address, index) => {
  //Looks up accounts and pulls the first one
  // const accounts = await drizzle.web3.eth.getAccounts();
  // const latestTweet = await drizzle.contracts.Twittor.methods
  //   .getNumTweets(accounts[0])
  //   .call();

  const result = await drizzle.contracts.Twittor.methods
    .getEverythingTweetStruct(address, index)
    .call();
  // console.log(result, 'RESULT');
  return result;
};

export default class SingleTweet extends React.Component {
  constructor(props) {
    super();

    this.state = {};
  }
  async componentDidMount() {
    // call the func here
    const tweetData = await this.getData(this.props.address, this.props.index);
    this.setState(tweetData);
  }

  getData = async (address, index) => {
    //Looks up accounts and pulls the first one
    // const accounts = await drizzle.web3.eth.getAccounts();
    // const latestTweet = await drizzle.contracts.Twittor.methods
    //   .getNumTweets(accounts[0])
    //   .call();

    const result = await drizzle.contracts.Twittor.methods
      .getEverythingTweetStruct(address, index)
      .call();
    return result;
  };

  render() {
    console.log(this.state, 'STATE');
    return (
      <div>
        <h1>Single</h1>
        <p>Address: {this.props.address}</p>
        <p>Block Num: {this.state[2]}</p>
        <p>Replies: {this.state[3]}</p>
        <p>Tweet: {this.state[1]}</p>
      </div>
    );
  }
}
