import React from 'react';
import { Drizzle } from 'drizzle';
import options from './drizzleOptions';
import ReplyTweet from './ReplyTweet';

const drizzle = new Drizzle(options);

export default class SingleTweet extends React.Component {
  constructor(props) {
    super();

    this.state = { reply: '' };
  }
  async componentDidMount() {
    await this.props.drizzle.contracts.Twittor.methods.getNumReplies.cacheCall(
      this.props.address,
      this.props.index
    );

    const tweetData = await this.getData(this.props.address, this.props.index);
    this.setState(tweetData);
  }

  getData = async (address, index) => {
    const result = await drizzle.contracts.Twittor.methods
      .getEverythingTweetStruct(address, index)
      .call();
    return result;
  };

  getReplyNum = async () => {
    // console.log('props ', this.props);
    const numReplies = await this.props.drizzle.contracts.Twittor.methods
      .getNumReplies(this.state.userAddress, this.props.index)
      .call();

    // const { drizzle } = this.props;

    console.log(drizzle.contracts.Twittor.methods, 'METHODS');
    const numR = await this.props.drizzle.contracts.Twittor.methods
      .getNumReplies(this.props.address, this.props.index)
      .cacheCall();
    console.log(numR, 'NUMR');

    this.setState({ numReplies });
    // console.log('numTweets', numTweets);
    this.forceUpdate();
  };

  handleInputChange = event => {
    this.setState({
      reply: event.target.value,
    });
    console.log(this.state);
  };

  handleSubmit = async event => {
    event.preventDefault();
    console.log(this.state, 'WHAT IS BEING SUBMITTED');
    await this.props.drizzle.contracts.Twittor.methods
      .addReply(this.props.address, this.props.index, this.state.reply)
      .send({ from: this.props.address });
  };

  render() {
    const { drizzleState } = this.props;
    let length = 0;

    // console.log(drizzleState.contracts.Twittor.getNumReplies, 'REPLY');
    const key = Object.keys(drizzleState.contracts.Twittor.getNumReplies)[0];

    if (drizzleState.contracts.Twittor.getNumReplies[key]) {
      length = drizzleState.contracts.Twittor.getNumReplies[key].value;
      // this.setState({ 3: length });
    }

    let mapArray = [];
    if (length) {
      mapArray.length = length;
      mapArray.fill(1);
    }

    // console.log(this.props.drizzle);
    return (
      <div>
        <h1>Single</h1>
        <p>Address: {this.props.address}</p>
        <p>Block Num: {this.state[2]}</p>
        <p>Replies: {this.state[3]}</p>

        <div>
          <button
            onClick={() =>
              this.setState({ displayReply: !this.state.displayReply })
            }
          >
            Reply
          </button>
          {this.state.displayReply ? (
            <div style={{ border: 'solid' }}>
              <form onSubmit={this.handleSubmit}>
                <input
                  key="reply"
                  name="reply"
                  value={this.state.reply}
                  placeholder="reply"
                  onChange={this.handleInputChange}
                />
                <button type="submit">Reply</button>
              </form>
              {length ? (
                mapArray
                  .map((tweet, idx) => {
                    return (
                      <ReplyTweet
                        address={this.props.address}
                        index={this.props.index}
                        replyIndex={idx}
                        key={idx}
                      />
                    );
                  })
                  .reverse()
              ) : (
                <h2>Length is false</h2>
              )}
            </div>
          ) : (
            <h1>False</h1>
          )}
        </div>

        <p>Tweet: {this.state[0]}</p>
      </div>
    );
  }
}
