import React from 'react';
import { Drizzle } from 'drizzle';
import options from './drizzleOptions';

const drizzle = new Drizzle(options);
const getData = async () => {
  const accounts = await drizzle.web3.eth.getAccounts();
  console.log(accounts[0], 'accounts');

  const result = await drizzle.contracts.Twittor.methods
    .getEverythingTweetStruct(accounts[0], 2)
    .call();
  console.log(result, 'RESULT');
  // console.log(
  //
  // );
  // return await drizzle.contracts.Twittor.methods.getData().call();
};

const randoFunc = async () => {
  console.log('>>>>>>>>', await getData());
};

